#include <luastate.h>
using namespace cpplua;

int main() {
  LuaState L;
  L.getGlobal("print");
  L.pushString("Hello World");
  L.pcall(1, 0, 0);

  return 0;
}

#include <iostream>
#include <luastate.h>
using namespace cpplua;

int main() {
  LuaState L;
  L.newTable();
  L.pushNumber(0);
  L.pushNumber(15);
  L.setTable();
  L.pushNumber(1);
  L.pushNumber(30);
  L.setTable();

  for(int i = 0; i < 2; i++) {
    L.pushNumber(i);
    L.getTable();
    std::cout << L.toNumber<int>() << std::endl;
    L.pop();
  }

  return 0;
}

#include <cpplua.h>
using namespace cpplua;

int main() {
  LuaState L;
  LuaObject luaPrint = L.global("print");
  luaPrint("Hello World");

  return 0;
}

#include <cpplua.h>
#include <cassert>
using namespace cpplua;

int main() {
  LuaState L;
  LuaObject table = L.emptyTable();
  for(int i = 0; i < 10; i++)
    table[i] = i*i;
  table["hello"] = -1.8;
  LuaObject table2 = L.emptyTable();
  table[table2] = "A table index";
  table["x"] = table2;
  table2[0] = 3;

  assert(9 , table[table["x"][0]]);
}
